INSERT INTO `faqs` (`faqID`, `languageID`, `title`, `description`, `sort`, `created_at`, `updated_at`) VALUES
	(1, 1, 'Как играть?', 'Выбери квест в своём городе, собери команду и приступай к игре! Вопросы и задания ты увидишь прямо в приложении, а ответы придётся искать в городе. В целом - ничего сложного, но при этом очень увлекательно!\r\nПеред началом первого квеста вы увидите небольшое обучение.', 0, '2016-08-19 22:26:30', '2017-11-30 10:11:56'),
	(2, NULL, 'Когда можно пройти квест?', 'В любой момент! Квесты доступны в приложении круглосуточно, и только ты решаешь, когда начать игру. Не нужно ничего бронировать, да и опоздать на наш квест никак не получится.', 3, '2016-08-19 22:26:30', '2017-11-13 22:50:28'),
	(3, NULL, 'Как подготовиться к квесту?', 'Никакая особенная подготовка не требуется. Советуем надеть удобную обувь и как следует зарядить гаджет: он станет твоим проводником в удивительный мир игры, а это непростая работа :)', 2, '2016-08-19 22:26:30', '2017-11-13 22:50:32'),
	(4, 2, 'Сколько человек может играть?', 'Идеальная команда для спасения мира - 2-6 человек. Но ты можешь и играть в одиночку, если уверен в своих силах.\r\nА если планируется большая компания, можно заказать групповой квест: в этом случае вы разделитесь на команды, опытный ведущий встретит и поможет влиться в игру, а для победителей мы подготовим приятные призы. Подробная информация - в разделе "Для групп".', 1, '2016-08-19 22:26:30', '2017-11-30 10:12:04');
