INSERT INTO `pages` (`pageID`, `languageID`, `name`, `alias`, `text`, `image`, `created_at`, `updated_at`) VALUES
	(1, 1, 'Для Групп', 'for_groups', 'Street Adventure – идеальное решение для корпоратива, праздника или школьной поездки!\r\n\r\nКвест - это и весёлая игра, и тимбилдинг, и познавательное путешествие по городу. А для больших групп (от 6 человек) мы предлагаем проведение квеста в корпоративном формате!\r\nВ точке начала квеста участников встречает ведущий-игротехник. Он проводит весёлую игровую разминку и делит гостей на команды, каждая из которых активирует квест в приложении. Команды ищут ответы в городе, а ведущий сопровождает весь процесс квеста, направляет игроков и модерирует игру.\r\nСчет ведется в режиме реального времени. Сразу после игры можно проводить награждение: кто лидирует в общем зачете – ясно сразу!\r\n\r\nБольшой компанией вы можете сыграть как в один из готовых квестов, так и в принципиально новый, созданный специально для вашего события! Более чем восьмилетний опыт работы в корпоративном сегменте позволяет нам быстро готовить новые сценарии, не изменяя при этом стандартам качества. И мы всегда рады нестандартным, интересным проектам :)\r\n\r\nСвяжитесь с нами, и менеджер с удовольствием проконсультирует по всем вопросам!', NULL, '2016-08-19 22:26:18', '2017-09-04 16:03:52'),
	(2, 1, 'Возможности', 'capabilities', 'Квест - это не просто прогулка для небольшой команды!\r\nС нашей помощью вы также можете:\r\n- провести корпоратив, праздник или школьный выезд!\r\n- создать уникальный квест для своего события!\r\n- организовать рекламную акцию!\r\n- и многое другое :)\r\nСвяжитесь с нами, чтобы узнать подробности.', NULL, '2016-08-19 22:26:18', '2017-05-19 12:17:49'),
	(3, 1, 'О компании', 'about_company', 'Street Adventure основан в 2008 году.\r\n\r\nВ это время основатель проекта Никита Богданов, будучи студентом, подрабатывал администратором в одном из московских хостелов. Большинство гостей хостела были иностранными туристами. Спустя пару месяцев общения с ними Никита ясно понял, что практически все они не видят «настоящую» Москву, ту Москву, в которой живем мы с вами. Они лишь передвигаются истоптанными улицами внутри Садового кольца и ходят по кафе с английским меню.\r\nНикита создал игру, суть которой сводилась к поиску матрешек, спрятанных в интересных точках «реальной» Москвы. Задача была собрать всю коллекцию.\r\n\r\nЧерез какое-то время мы решили автоматизировать процесс и отправлять задания по SMS, превратив игру в интерактивную квест-экскурсию. И неожиданно по объявлениям стали звонить не иностранцы, а москвичи! Тогда мы поняли, что делали что-то не то :) и переключили тумблер на вас, дорогие сограждане!\r\n\r\nЗа следующие годы мы полностью перешли с SMS на интернет-версию игры. А теперь запустили собственное мобильное приложение. Мы постоянно развиваем игровую систему, придумываем новые задания: интересные, веселые, а часто и безумные. Сегодня на нашей платформе более пятнадцати различных типов заданий, и по функционалу не имеет аналогов в мире. Наша команда всегда открыта для обратной связи и с радостью воплощает ваши предложения в жизнь.\r\n\r\nНадеемся, что Street Adventure станет вашим любимым способом здорово проводить время с друзьями и близкими!\r\nИграйте вживую :)', 'ff93ee66036cea48167a5149bd702a5b.jpg', '2016-08-19 22:26:18', '2017-11-30 11:10:33'),
	(4, 1, 'Контакты', 'contacts', 'Всегда рады ответить на все Ваши вопросы! :)', NULL, '2016-08-19 22:26:18', '2017-05-19 12:35:00'),
